        AND create_date = #{condition.createDate}

UPDATE zgk_expert_info
expert_name = #{expertName}, expert_photo_url = #{expertPhotoUrl}, expert_intro = #{expertIntro}, expert_phone = #{expertPhone}, server_cases = #{serverCases}, area_id = #{areaId}, expert_domain = #{expertDomain}, expert_profile = #{expertProfile}, best_expert = #{bestExpert}, is_checked = #{isChecked}, create_date = #{createDate},
WHERE id = #{id}

