        discipline_sub_name = #{update.disciplineSubName},


        major_code = #{update.majorCode},


        major_name = #{update.majorName},


        major_note = #{update.majorNote},


        salary_rank = #{update.salaryRank},


        employmentRate = #{update.employmentRate},





