mapper.delete:
DELETE FROM zgk_major_base


        AND id = #{id}


        AND major_type = #{majorType}


        AND discipline_code = #{disciplineCode}


        AND discipline_name = #{disciplineName}


        AND discipline_sub_code = #{disciplineSubCode}


        AND discipline_sub_name = #{disciplineSubName}


        AND major_code = #{majorCode}


        AND major_name = #{majorName}


        AND major_note = #{majorNote}


        AND salary_rank = #{salaryRank}


        AND employmentRate = #{employmentRate}

