        AND vocational_demand = #{vocationalDemand}


        AND career_prospects = #{careerProspects}


        AND createDate = #{createDate}


        AND creator = #{creator}


        AND lastModDate = #{lastModDate}


        AND lastModifier = #{lastModifier}


        AND is_delete = #{isDelete}

