mapper.select:
SELECT
FROM gk_video_course course LEFT OUTER JOIN gk_video_classify classify ON classify.id = course.classifyId LEFT OUTER JOIN gk_subject gksubject ON course.subjectId = gksubject.id LEFT JOIN gk_province province ON course.areaId = province.id
course.classifyId=1
and id in (#{condition.whereSql})
${condition.groupOp} ${condition.coursesubjectId.field} ${condition.coursesubjectId.op} #{condition.coursesubjectId.data}
${condition.groupOp} ${condition.courseareaId.field} ${condition.courseareaId.op} #{condition.courseareaId.data}
and (course.teacher like CONCAT('%', #{condition.queryparam.data} , '%') OR course.subcontent like CONCAT('%', #{condition.queryparam.data} , '%')')
ORDER BY id DESC
limit ${offset}, ${rows}

SELECT count(course.id) FROM gk_video_course course LEFT OUTER JOIN gk_video_classify classify ON classify.id = course.classifyId LEFT OUTER JOIN gk_subject gksubject ON course.subjectId = gksubject.id LEFT JOIN gk_province province ON course.areaId = province.id
course.classifyId=1
${groupOp} ${coursesubjectId.field} ${coursesubjectId.op} #{coursesubjectId.data}
${groupOp} ${course.courseareaId.field} ${course.courseareaId.op} #{course.courseareaId.data}
and (course.teacher like CONCAT('%', #{queryparam.data} , '%') OR course.subcontent like CONCAT('%', #{queryparam.data} , '%')')
