mapper.select:
SELECT

FROM gk_examination_paper paper
LEFT OUTER JOIN gk_subject gksubject on paper.subjectId = gksubject.id
LEFT JOIN gk_province province ON province.id = paper.areaId



        and id in (#{condition.whereSql})



        ${condition.groupOp}   ${condition.paper.subjectId.field}  ${condition.paper.subjectId.op}  #{condition.paper.subjectId.data}


        ${condition.groupOp}   ${condition.paper.years.field}  ${condition.paper.years.op}  #{condition.paper.years.data}


        ${condition.groupOp}   ${condition.paper.areaId.field}  ${condition.paper.areaId.op}  #{condition.paper.areaId.data}


        ${condition.groupOp}   ${condition.queryparam.field}  ${condition.queryparam.op}  #{condition.queryparam.data}





ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

SELECT
COUNT(paper.id)
FROM gk_examination_paper paper
LEFT OUTER JOIN gk_subject gksubject on paper.subjectId = gksubject.id
LEFT JOIN gk_province province ON province.id = paper.areaId


       ${groupOp}   ${subjectId.field}  ${subjectId.op}  #{paper.subjectId.data}


       ${groupOp}   ${paper.years.field}  ${paper.years.op}  #{paper.years.data}


       ${groupOp}   ${areaId.field}  ${paper.areaId.op}  #{paper.areaId.data}


       ${groupOp}   ${queryparam.field}  ${queryparam.op}  #{queryparam.data}
