expert_id = #{expertId}, user_id = #{userId}, user_name = #{userName}, user_question = #{userQuestion}, user_answer = #{userAnswer}, create_date = #{createDate},
WHERE id = #{id}

mapper.delete:
DELETE FROM zgk_expert_user_question


        AND id = #{id}


        AND expert_id = #{expertId}


        AND user_id = #{userId}


        AND user_name = #{userName}


        AND user_question = #{userQuestion}


        AND user_answer = #{userAnswer}


        AND create_date = #{createDate}

