mapper.select:
SELECT

FROM gk_gkinformation_gkhot gkhot
LEFT JOIN gk_province province on gkhot.areaId=province.id



        and id in (#{condition.whereSql})



        ${condition.groupOp}   ${condition.gkhot.hotInformation.field}  ${condition.gkhot.hotInformation.op}  #{condition.gkhot.hotInformation.data}


        ${condition.groupOp}   ${condition.gkhot.informationContent.field}  ${condition.gkhot.informationContent.op}  #{condition.gkhot.informationContent.data}


        ${condition.groupOp}   ${condition.gkhot.areaId.field}  ${condition.gkhot.areaId.op}  #{condition.gkhot.areaId.data}





ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

SELECT count(gkhot.id)
FROM gk_gkinformation_gkhot gkhot
LEFT JOIN gk_province province on gkhot.areaId=province.id



       ${groupOp} ${gkhot.hotInformation.field} ${gkhot.hotInformation.op}  #{gkhot.hotInformation.data}


       ${groupOp} ${gkhot.informationContent.field} ${gkhot.informationContent.op}  #{gkhot.informationContent.data}


       ${groupOp} ${gkhot.areaId.field} ${gkhot.areaId.op}  #{gkhot.areaId.data}
