mapper.select:
SELECT

FROM zgk_major major
LEFT OUTER JOIN zgk_major_detail detail ON detail.id=major.id
LEFT JOIN zgk_majored_category  genre ON genre.id=major.discipline_categories
LEFT JOIN zgk_majored_category  category ON category.id=major.major_category
LEFT JOIN zgk_majored_category  type ON type.id=major.major_type AND (type.id=0 OR type.id=1)


    1=1

        and id in (#{condition.whereSql})


        ${condition.groupOp}   ${condition.subjectType.field}  ${condition.subjectType.op}  #{condition.subjectType.data}


        ${condition.groupOp}
        (code like #{condition.queryparam.data}
        OR name like #{condition.queryparam.data}
        OR name like #{condition.queryparam.data}
        OR salaryRank like #{condition.queryparam.data}
        OR employedRank like #{condition.queryparam.data}
        OR majoredType like #{condition.queryparam.data})




    ORDER BY id DESC

    limit ${offset}, ${rows}

        SELECT count(major.id)        FROM zgk_major major
        LEFT OUTER JOIN zgk_major_detail detail ON detail.id=major.id
        LEFT JOIN zgk_majored_category  genre ON genre.id=major.discipline_categories
        LEFT JOIN zgk_majored_category  category ON category.id=major.major_category
        LEFT JOIN zgk_majored_category  type ON type.id=major.major_type AND (type.id=0 OR type.id=1)

1=1

                ${groupOp}   ${subjectType.field}  ${subjectType.op}  #{subjectType.data}


                ${groupOp}
                (code like #{queryparam.data}
                OR name like #{queryparam.data}
                OR name like #{queryparam.data}
                OR salaryRank like #{queryparam.data}
                OR employedRank like #{queryparam.data}
                OR majoredType like #{queryparam.data}
                OR subjectType like #{queryparam.data})
