mapper.select:
SELECT

FROM gk_schedule schedule
LEFT JOIN gk_province province ON province.id=schedule.areaId



        and id in (#{condition.whereSql})



        ${condition.groupOp}   ${condition.scheduleareaId.field}  ${condition.scheduleareaId.op}  #{condition.scheduleareaId.data}


        ${condition.groupOp}  (province.name like #{condition.queryparam.data} OR schedule.month like #{condition.queryparam.data} OR schedule.years like #{condition.queryparam.data} )






    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

SELECT count(schedule.id) FROM gk_schedule schedule LEFT JOIN gk_province province ON province.id=schedule.areaId
${groupOp} ${scheduleareaId.field} ${scheduleareaId.op} #{scheduleareaId.data}
${groupOp} (province.name like #{queryparam.data} OR schedule.month like #{queryparam.data} OR schedule.years like #{queryparam.data})
