mapper.select:
SELECT

FROM
(select * FROM zgk_university uu
where 1=1

    ${condition.groupOp} uu.name ${condition.universityname.op}  #{condition.universityname.data}


    ${condition.groupOp} uu.id ${condition.universityid.op}  #{condition.universityid.data}


    ${condition.groupOp} uu.property ${condition.universityproperty.op}  #{condition.universityproperty.data}

ORDER BY  uu.lastModDate DESC

    limit 10

) university
LEFT OUTER JOIN zgk_university_enrolling enrolling ON university.id=enrolling.university_id
LEFT JOIN zgk_data_dict batchdict ON batchdict.dictId=enrolling.batch AND batchdict.type='MAJOR_BATCH'
LEFT JOIN zgk_data_dict typedict ON typedict.dictId=enrolling.university_major_type AND typedict.type='UNIVERSITY_MAJOR_TYPE'

    1=1

        and id in (#{condition.whereSql})


        AND
        (
        province.name  LIKE  #{condition.queryparam.data},
        OR batchdict.name  LIKE  #{condition.queryparam.data},
        OR typedict.name  LIKE  #{condition.queryparam.data},
        OR enrolling.year  LIKE  #{condition.queryparam.data}
        )


        ${condition.groupOp} enrolling.year ${condition.enrollingyear.op}  #{condition.enrollingyear.data}


        ${condition.groupOp} university.areaid ${condition.universityareaid.op}  #{condition.universityareaid.data}



        ${condition.groupOp} enrolling.batch ${condition.enrollingbatch.op}  #{condition.enrollingbatch.data}


        ${condition.groupOp} enrolling.university_major_type ${condition.enrollinguniversityMajorType.op}  #{condition.enrollinguniversityMajorType.data}


        ${condition.groupOp} enrolling.university_major_type ${condition.entype.op}  #{condition.entype.data}


ORDER BY  university.id DESC

    limit ${offset}, ${rows}

SELECT count(enrolling.id)  FROM
zgk_university_enrolling enrolling
LEFT OUTER JOIN zgk_university university ON university.id=enrolling.university_id

LEFT JOIN zgk_data_dict batchdict ON batchdict.dictId=enrolling.batch AND batchdict.type='MAJOR_BATCH'


LEFT JOIN zgk_data_dict typedict ON typedict.dictId=enrolling.batch AND typedict.type='UNIVERSITY_MAJOR_TYPE'



        AND
        (
        province.name  LIKE  #{queryparam.data},
        OR batchdict.name  LIKE  #{queryparam.data},
        OR typedict.name  LIKE  #{queryparam.data},
        OR enrolling.year  LIKE  #{queryparam.data},
        )


        ${groupOp} enrolling.year ${enrollingyear.op}  #{enrollingyear.data}


        ${groupOp} university.areaid ${universityareaid.op}  #{universityareaid.data}


        ${groupOp} university.name ${universityname.op}  #{universityname.data}


        ${groupOp} university.property ${universityproperty.op}  #{universityproperty.data}


        ${groupOp} enrolling.batch ${enrollingbatch.op}  #{enrollingbatch.data}


        ${groupOp} enrolling.university_major_type ${enrollinguniversityMajorType.op}  #{enrollinguniversityMajorType.data}


        ${groupOp} enrolling.university_major_type ${entype.op}  #{entype.data}

SELECT
FROM zgk_university_enrolling enrolling LEFT OUTER JOIN zgk_university university ON university.id=enrolling.university_id LEFT JOIN zgk_data_dict batchdict ON batchdict.dictId=enrolling.batch AND batchdict.type='MAJOR_BATCH' LEFT JOIN zgk_data_dict typedict ON typedict.dictId=enrolling.university_major_type AND typedict.type='UNIVERSITY_MAJOR_TYPE'
enrolling.id = #{id}
