mapper.select:
SELECT



                    , id


                    , university_id


                    , major_name


                    , year


                    , batch


                    , university_major_type


                    , plan_enrolling_number


                    , real_enrolling_number


                    , length_of_schooling


                    , school_fee


                    , highest_score


                    , highest_precedence


                    , lowest_score


                    , lowest_precedence


                    , average_score


                    , average_precedence


                    , createDate


                    , creator


                    , lastModDate


                    , lastModifier


                    , is_delete






FROM zgk_university_enrolling



        and id in (#{condition.whereSql})



        ${condition.groupOp}   ${condition.id.field}  ${condition.id.op}  #{condition.id.data}


        ${condition.groupOp}   ${condition.universityId.field}  ${condition.universityId.op}  #{condition.universityId.data}


        ${condition.groupOp}   ${condition.majorName.field}  ${condition.majorName.op}  #{condition.majorName.data}


        ${condition.groupOp}   ${condition.year.field}  ${condition.year.op}  #{condition.year.data}


        ${condition.groupOp}   ${condition.batch.field}  ${condition.batch.op}  #{condition.batch.data}


        ${condition.groupOp}   ${condition.universityMajorType.field}  ${condition.universityMajorType.op}  #{condition.universityMajorType.data}


        ${condition.groupOp}   ${condition.planEnrollingNumber.field}  ${condition.planEnrollingNumber.op}  #{condition.planEnrollingNumber.data}


        ${condition.groupOp}   ${condition.realEnrollingNumber.field}  ${condition.realEnrollingNumber.op}  #{condition.realEnrollingNumber.data}


        ${condition.groupOp}   ${condition.lengthOfSchooling.field}  ${condition.lengthOfSchooling.op}  #{condition.lengthOfSchooling.data}


        ${condition.groupOp}   ${condition.schoolFee.field}  ${condition.schoolFee.op}  #{condition.schoolFee.data}


        ${condition.groupOp}   ${condition.highestScore.field}  ${condition.highestScore.op}  #{condition.highestScore.data}


        ${condition.groupOp}   ${condition.highestPrecedence.field}  ${condition.highestPrecedence.op}  #{condition.highestPrecedence.data}


        ${condition.groupOp}   ${condition.lowestScore.field}  ${condition.lowestScore.op}  #{condition.lowestScore.data}


        ${condition.groupOp}   ${condition.lowestPrecedence.field}  ${condition.lowestPrecedence.op}  #{condition.lowestPrecedence.data}


        ${condition.groupOp}   ${condition.averageScore.field}  ${condition.averageScore.op}  #{condition.averageScore.data}


        ${condition.groupOp}   ${condition.averagePrecedence.field}  ${condition.averagePrecedence.op}  #{condition.averagePrecedence.data}


        ${condition.groupOp}   ${condition.createDate.field}  ${condition.createDate.op}  #{condition.createDate.data}


        ${condition.groupOp}   ${condition.creator.field}  ${condition.creator.op}  #{condition.creator.data}


        ${condition.groupOp}   ${condition.lastModDate.field}  ${condition.lastModDate.op}  #{condition.lastModDate.data}


        ${condition.groupOp}   ${condition.lastModifier.field}  ${condition.lastModifier.op}  #{condition.lastModifier.data}


        ${condition.groupOp}   ${condition.isDelete.field}  ${condition.isDelete.op}  #{condition.isDelete.data}





ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}
