mapper.select:
SELECT
from gk_university university LEFT JOIN gk_province province ON province.name = university.provinceName LEFT JOIN gk_university_dict dict ON university.property=dict.dictId and dict.type='FEATURE' LEFT JOIN gk_university_sort sort ON sort.universityId=university.id LEFT OUTER JOIN gk_university_dict dict1 ON university.educationLevel=dict1.dictId and dict1.type='EDULEVEL'
and id in (#{condition.whereSql})
ORDER BY ${orderBy} ${sortBy}
limit ${offset}, ${rows}

SELECT count(university.id) from gk_university university LEFT JOIN gk_province province ON province.id = university.provinceId
${groupOp} ${scheduleareaId.field} ${scheduleareaId.op} #{scheduleareaId.data}
${groupOp} (province.name like #{queryparam.data} OR schedule.month like #{queryparam.data} OR schedule.years like #{queryparam.data})
