SELECT
*
from zgk_university_dict dict WHERE dict.type='FEATURE' AND name=#{name}

select en.universityId as universityId, un.`name` as universityName, un.subjection as province, un.rank as rank, un.property as feature, en.batch as batch, en.areaId as areaId, Case When en.majorType='1' then '文史' When en.majorType='2' then '理工' else '' end as majorType, Case when GROUP_CONCAT(en.averageScore order by en.averageScore desc SEPARATOR '@@') is null then '' else GROUP_CONCAT(en.averageScore order by en.averageScore desc SEPARATOR '@@') end as averageScoreList, Case when GROUP_CONCAT(en.lowestScore order by en.lowestScore desc SEPARATOR '@@') is null then '' else GROUP_CONCAT(en.lowestScore order by en.lowestScore desc SEPARATOR '@@') end as lowestScoreList from zgk_university_enrolling en left join zgk_university un on en.universityId = un.id
en.universityId !='0'
AND en.areaId = #{areaId}
AND en.batch in ('1','19')
AND en.batch in ('2','15','16','20','23')
AND en.batch in ('3','17','18','21')
AND en.batch in ('8','9','10','11','12','13','14','22')
AND en.majorType = #{majorType}
AND en.year >= #{startYear}
AND en.year <= #{endYear}
group by en.universityId,un.`name`,en.batch,en.areaId,en.majorType order by (case when un.rank is null then '3000' else un.rank end + 0)

select un.id as universityId,un.`name` as universityName,en.`year`, en.batch as batch, CASE WHEN en.averageScore='-' then '0' WHEN en.averageScore is null then '0' ELSE en.averageScore end as avgScore, CASE WHEN en.lowestScore='-' then '0' WHEN en.lowestScore is null then '0' ELSE en.lowestScore end as minScore, en.realEnrollingNumber as enrollingNumber from zgk_university_enrolling en left join zgk_university un on un.id= en.universityId
1=1
AND en.areaId = #{areaId}
AND en.batch in ('1','19')
AND en.batch in ('2','15','16','20','23')
AND en.batch in ('3','17','18','21')
AND en.batch in ('8','9','10','11','12','13','14','22')
AND un.`name` = #{universityName}
AND en.majorType = #{majorType}
AND en.year >= #{startYear}
AND en.year <= #{endYear}
order by averageScore desc
