mapper.select:
SELECT

from gk_university university
LEFT JOIN gk_province province  ON province.name = university.provinceName
LEFT JOIN gk_university_dict dict ON university.property=dict.dictId and dict.type='FEATURE'
LEFT JOIN gk_university_sort sort ON sort.universityId=university.id
LEFT OUTER JOIN gk_university_dict dict1 ON university.educationLevel=dict1.dictId and dict1.type='EDULEVEL'



        and id in (#{condition.whereSql})


        ${condition.groupOp}   ${condition.universityeducationLevel.field}  ${condition.universityeducationLevel.op}  #{condition.universityeducationLevel.data}


        ${condition.groupOp}   ${condition.universityprovinceName.field}  ${condition.universityprovinceName.op}  #{condition.universityprovinceName.data}





        ${condition.groupOp}   (province.name like #{condition.queryparam.data}
        OR university.code like #{condition.queryparam.data}
        OR university.name like #{condition.queryparam.data}
        OR university.subjection like #{condition.queryparam.data}
        OR university.type like #{condition.queryparam.data}
        OR university.provinceName like #{condition.queryparam.data}
        OR dict1.name like #{condition.queryparam.data})



    ORDER BY id DESC

    limit ${offset}, ${rows}

SELECT count(university.id)
from gk_university university
LEFT JOIN gk_province province  ON province.name = university.provinceName
LEFT JOIN gk_university_dict dict ON university.property=dict.dictId and dict.type='FEATURE'
LEFT JOIN gk_university_sort sort ON sort.universityId=university.id
LEFT JOIN gk_university_dict dict1 ON university.educationLevel=dict1.dictId and dict1.type='EDULEVEL'


        ${groupOp}   ${universityeducationLevel.field}  ${universityeducationLevel.op}  #{universityeducationLevel.data}


        ${groupOp}   ${universityprovinceName.field}  ${universityprovinceName.op}  #{universityprovinceName.data}





        ${groupOp}   (province.name like #{queryparam.data}
        OR university.code like #{queryparam.data}
        OR university.name like #{queryparam.data}
        OR university.subjection like #{queryparam.data}
        OR university.type like #{queryparam.data}
        OR university.provinceName like #{queryparam.data}
        OR dict1.name like #{queryparam.data})

SELECT
*
from gk_university_dict dict WHERE dict.type='FEATURE' AND name=#{name}
