SELECT



                    , id


                    ,
                    (SELECT name FROM zgk_university uu WHERE uu.id = university_id limit 1) as nuiversityName


                    ,
                    (SELECT major_name FROM zgk_major uu WHERE uu.id = major_id limit 1) as majorName


                    , year


                    ,
                    (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'BATCHTYPE' AND dd.dictId = batch) as batchName


                    ,
                    (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'ADMISSION_BATCH_TYPE' AND dd.dictId = admissionBatchId) as admissionBatchName


                    , (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'MAJOR_TYPE' AND dd.dictId = universityMajorType) as majorTypeName


                    , plan_enrolling_number


                    , real_enrolling_number


                    , length_of_schooling


                    , school_fee


                    , highest_score


                    , highest_precedence


                    , lowest_score


                    , lowest_precedence


                    , average_score


                    , average_precedence


                    , createDate


                    , creator


                    , lastModDate


                    , lastModifier


                    , is_delete


                    , areaId




    id as id ,
    (SELECT name FROM zgk_province where id = areaId) as province,
    university_id as universityId ,
    (SELECT name FROM zgk_university uu WHERE uu.id = university_id limit 1) as universityName
    , (SELECT major_name FROM zgk_major uu WHERE uu.id = major_id limit 1) as majorName,
    major_id as majorId ,
    year as year ,
    (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'BATCHTYPE' AND dd.dictId = batch) as batchName
    , (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'ADMISSION_BATCH_TYPE' AND dd.dictId = admissionBatchId) as admissionBatchName
    , (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'UNIVERSITY_MAJOR_TYPE' AND dd.dictId = university_major_type) as majorTypeName,
    plan_enrolling_number as planEnrollingNumber ,
    real_enrolling_number as realEnrollingNumber ,
    (SELECT name FROM zgk_data_dict dd WHERE dd.type = 'LENGTH_OF_SCHOOLING' AND dd.dictId = length_of_schooling) as lengthOfSchoolingName,
    school_fee as schoolFee ,
    highest_score as highestScore ,
    highest_precedence as highestPrecedence ,
    lowest_score as lowestScore ,
    lowest_precedence as lowestPrecedence ,
    average_score as averageScore ,
    average_precedence as averagePrecedence ,
    createDate as createDate ,
    creator as creator ,
    lastModDate as lastModDate ,
    lastModifier as lastModifier ,
    is_delete as isDelete,
    areaId as areaId

FROM zgk_university_major_enrolling
LEFT JOIN (select id as uid,name as universityName FROM zgk_university) uu ON uu.uid=university_id



